package com.example.animallistview;

import android.os.Bundle;
import android.view.View;
import android.widget.AdapterView;
import android.widget.ListView;
import android.widget.Toast;

import androidx.appcompat.app.AppCompatActivity;

import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {

    private ListView lvAnimals;
    // Du lieu
    private List<Animal> dataSource;

    private AnimalAdapter animalAdapter;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        lvAnimals = findViewById(R.id.lvAnimals);
        // Fake du lieu
        fakeData();
        // Tao adapter
        animalAdapter = new AnimalAdapter(this, dataSource);
        // Set adapter cho listview
        lvAnimals.setAdapter(animalAdapter);

        lvAnimals.setOnItemClickListener(new AdapterView.OnItemClickListener() {
            @Override
            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {
                Animal animal = dataSource.get(position);
                Toast.makeText(MainActivity.this, animal.name.toUpperCase(), Toast.LENGTH_SHORT).show();
            }
        });
        lvAnimals.setOnItemLongClickListener(new AdapterView.OnItemLongClickListener() {
            @Override
            public boolean onItemLongClick(AdapterView<?> parent, View view, int position, long id) {
                dataSource.remove(position);
                // Thong bao cho adapter du lieu thay doi -> render listview
                animalAdapter.notifyDataSetChanged();
                Toast.makeText(MainActivity.this, "Xoa", Toast.LENGTH_SHORT).show();
                return false;
            }
        });

    }
    private void fakeData() {
        dataSource = new ArrayList<>();
        dataSource.add(new Animal("Cat", "data:image/jpeg;base64," ));
        dataSource.add(new Animal("Dog", "data:image/jpeg;base64,"));
        dataSource.add(new Animal("Elephant", "https://i1.sndcdn.com/artworks-000475366593-n1iask-t500x500.jpg"));
        dataSource.add(new Animal("Lion", "data:image/jpeg;base64,"));
        dataSource.add(new Animal("Monkey","https://i1.sndcdn.com/artworks-wJy2OjDt8YJxVP3X-AcWzzA-t500x500.jpg"));
        dataSource.add(new Animal("Tiger", "data:image/jpeg;base64,"));
    }
}